.caption {
	font-size: 1.2rem;
	font-weight: 400;
	color: rgba(0, 0, 0, 0.4);
	font-family: "Diatype Mono Variable";
	font-style: normal;
	line-height: 1.25;
	letter-spacing: 0em;
	display: block;
	font-variation-settings: 'slnt' 0, 'MONO' 1;
}

.caption a {
	color: rgba(0, 0, 0, 0.4);
	text-decoration: underline;
}

.caption a:hover {
}

media-item .caption {
	margin-top: .5em;
}

gallery-grid .caption,
gallery-columnized .caption,
gallery-justify .caption {
	margin-bottom: 2em;
}

[thumbnail-index] .caption {
	text-align: center;
}

[thumbnail-index] .caption .tags {
	margin-top: 0.25em;
}

.page {
	justify-content: center;
	background-color: #fafafa;
}

.page-content {
	padding: 3.4rem;
	text-align: left;
	border-width: 0rem;
	border-color: rgba(255, 149, 149, 0.63);
	border-style: solid;
	border-radius: 0rem;
	box-shadow: 0.0rem 0.0rem 0rem 0rem rgba(0, 0, 0, 0.25);
	padding-top: 2rem;
	padding-bottom: 2rem;
	padding-right: 6rem;
	padding-left: 6rem;
}

.mobile [id] .page-layout {
	max-width: 100%;
}

.page-layout {
	align-items: flex-start;
	max-width: 90%;
	padding: 0.0rem;
}

media-item::part(media) {
	border: 0;
	padding: 0;
}

.quick-view {
	height: 100%;
	width: 100%;
	padding: 3rem;
	margin-top: auto;
	margin-right: auto;
	margin-bottom: auto;
	margin-left: auto;
}

.quick-view-background {
	background-color: #ffffff;
}

.quick-view .caption {
	color: rgba(255, 255, 255, 1.0);
	padding: 20px 0;
	text-align: center;
	transition: 100ms opacity ease-in-out;
	position: absolute;
	bottom: 0;
	left: 0;
	right: 0;
}

.quick-view .caption-background {
	padding: 0.5rem 1rem;
	display: inline-block;
	background: rgba(0, 0, 0, 0.5);
	border-radius: .5rem;
	text-align: left;
	max-width: 50rem;
}

